package com.mokous.winexeproxy.utils;

import org.apache.commons.lang3.StringUtils;


/**
 * @author luofei (Your Name Here)
 * @date 2016年10月5日
 * 
 */
public class AppleAuthUtils {
    static String AUTHJSON = "{\\\"fairPlayCertificate\\\":\\\"\\\",\\\"fairPlayDeviceType\\\":\\\"82\\\",\\\"fairPlayGUID\\\":\\\"ecd2ecc6244384dc71ad44fbeaafa18999e4608a\\\",\\\"keyTypeSupportVersion\\\":\\\"492\\\",\\\"keydata\\\":\\\"\\\"}";

    public static String buildAuthCmdParameter(String exePath, String authJson, String appleId) {
        String cmd = exePath + " -op 2 -auth_info " + authJson + " -appleid " + appleId;
        return cmd;
    }

    public static String buildLaunchCmdParameter(String exePath, String appleId, String ikma, String ikmb) {
        String cmd = exePath + " -appleid " + appleId;
        if (!StringUtils.isEmpty(ikma) && !StringUtils.isEmpty(ikmb)) {
            cmd += "  -ikma " + ikma + " -ikmb " + ikmb;
        }
        return cmd;
    }

    public static String buildAuthPcCmdParameter(String exePath, String pwd, String appleId) {
        String cmd = exePath + " -op 6 -passwd  " + pwd + " -appleid " + appleId;
        return cmd;
    }

}
